mapper.select:
select g.code code, g.name name, g.unit unit, g.specifications specifications, g.id goodsId, i.amount amount, i.id inventoryId, g.producer producer, g.remark remark, g.image image, t.name typeName, t.id typeId from inventory i join goods g on i.goodsid = g.id join type t on g.typeid = t.id
and t.id = #{id}
and g.name like #{name}

mapper.update:
update goods set code = #{goods.code}, typeid =  #{goods.typeid}, name = #{goods.name},
                 unit = #{goods.unit}, specifications = #{goods.specifications}, producer = #{goods.producer},
                 remark = #{goods.remark}, image = #{goods.image}
where id = #{goods.id}
