mapper.select:
select * from instorage


        and type = #{type}



        and linkman like #{linkman}

mapper.update:
update outstorage
set company = #{in.company}, department = #{in.department}, phone = #{in.phone},
    linkman = #{in.linkman}, goodsids = #{in.goodsids}, amount = #{in.amount}
where id = #{in.id}
