mapper.select:
select o.*, d.name department, c.name company, u.name linkman from outstorage o join department d on o.departmentid = d.id join company c on o.companyid = c.id join user u on o.linkmanid = u.phone
and o.type = #{type}
and u.name like #{linkman}

mapper.update:
update outstorage
set companyid = #{out.companyid}, departmentid = #{out.departmentid}, linkmanid = #{out.linkmanid},
    phone = #{out.phone}, goodsids = #{out.goodsids}, amount = #{out.amount}, status = #{out.status}
where id = #{out.id}
